html:
{% set escalated_items = doc.get("custom_quotation_child_table") or [] %}

<div class="header">
    <h2>Agromayal Botanica, S.A. de C.V.</h2>
    <p>GUANACEVI # 245<br>
    PARQUE INDUSTRIAL GOMEZ PALACIO<br>
    GOMEZ PALACIO DURANGO , MEXICO 35078<br>
    PH : +52-871-750-0846<br>
    FX : +52-871-750-1379<br>
    Email: oscar@amb-wellness.com<br>
    Web: www.amb-wellness.com</p>
</div>

<h3>Quotation No: {{ doc.name }}</h3>

<div class="row">
    <div class="col-6">
        <strong>To:</strong><br>
        {{ doc.customer_name }}<br>
        {% if doc.contact_display %}{{ doc.contact_display }}<br>{% endif %}
        {{ doc.address_display }}
    </div>
    <div class="col-6 text-right">
        <strong>Date:</strong> {{ doc.transaction_date }}<br>
        <strong>Valid Till:</strong> {{ doc.valid_till }}<br>
        <strong>Currency:</strong> {{ doc.currency }}
    </div>
</div>

<hr>

<!-- Standard Items Table (if any) -->
{% if doc.items %}
<h4>Standard Items</h4>
<table class="table table-bordered">
    <thead>
        <tr>
            <th>Sr</th>
            <th>Item Code</th>
            <th>Description</th>
            <th>Quantity</th>
            <th>UOM</th>
            <th>Rate</th>
            <th>Amount</th>
        </tr>
    </thead>
    <tbody>
        {% for item in doc.items %}
        <tr>
            <td>{{ loop.index }}</td>
            <td>{{ item.item_code }}</td>
            <td>{{ item.item_name }}{% if item.description %}<br><small>{{ item.description }}</small>{% endif %}</td>
            <td>{{ item.qty }}</td>
            <td>{{ item.uom }}</td>
            <td class="text-right">{{ frappe.format(item.rate, {'fieldtype': 'Currency'}) }}</td>
            <td class="text-right">{{ frappe.format(item.amount, {'fieldtype': 'Currency'}) }}</td>
        </tr>
        {% endfor %}
    </tbody>
    <tfoot>
        <tr>
            <td colspan="6" class="text-right"><strong>Total:</strong></td>
            <td class="text-right"><strong>{{ frappe.format(doc.total, {'fieldtype': 'Currency'}) }}</strong></td>
        </tr>
    </tfoot>
</table>
{% endif %}

<!-- Escalated Pricing Table -->
{% if escalated_items %}
<h4>Escalated Pricing - Quantity Based</h4>
<table class="table table-bordered">
    <thead>
        <tr>
            <th>Sr</th>
            <th>Item Code</th>
            <th>Description</th>
            <th>Quantity Range</th>
            <th>EX Works Price</th>
            <th>Freight Cost</th>
            <th>Delivered Price</th>
            <th>Total Price Range</th>
            <th>Freight Type</th>
        </tr>
    </thead>
    <tbody>
        {% for item in escalated_items %}
        <tr>
            <td>{{ loop.index }}</td>
            <td>{{ item.item_code }}</td>
            <td>{{ item.item_name }}</td>
            <td>{{ item.quantity_range }}</td>
            <td class="text-right">{{ frappe.format(item.ex_works_price, {'fieldtype': 'Currency'}) }}/kg</td>
            <td class="text-right">{{ frappe.format(item.freight_cost, {'fieldtype': 'Currency'}) }}/kg</td>
            <td class="text-right">{{ frappe.format(item.delivered_price, {'fieldtype': 'Currency'}) }}/kg</td>
            <td class="text-right">
                {% set min_total = item.delivered_price * item.min_quantity %}
                {% set max_total = item.delivered_price * item.max_quantity %}
                {% if item.min_quantity == item.max_quantity %}
                    {{ frappe.format(min_total, {'fieldtype': 'Currency'}) }}
                {% else %}
                    {{ frappe.format(min_total, {'fieldtype': 'Currency'}) }} - {{ frappe.format(max_total, {'fieldtype': 'Currency'}) }}
                {% endif %}
            </td>
            <td>{{ item.freight_type }}</td>
        </tr>
        {% endfor %}
    </tbody>
</table>
{% endif %}

<!-- Totals Section -->
<div class="row">
    <div class="col-6">
        <h5>Terms & Conditions</h5>
        <ul class="list-unstyled">
            <li><strong>Quotation valid until:</strong> December 31st, 2025</li>
            <li><strong>Lead time of production:</strong> 7-10 Days</li>
            <li><strong>Lead time of transportation:</strong> 3 Days</li>
            <li><strong>Incoterm:</strong> DAP-France (Importer responsible for import Duties & Taxes)</li>
            <li><strong>Freight terms:</strong> Air Freight via UPS courier (Door to Door)</li>
            <li><strong>HS CODE:</strong> 1302.19.99</li>
            <li><strong>Payment terms:</strong> 60 DAYS NET</li>
        </ul>
        
        <h5>Packaging:</h5>
        <p><strong>Standard:</strong> 25kg in double polyethylene bags inside carton fiber drum<br>
        <strong>Individual:</strong> 1kg in metalized bag</p>
        
        <p><strong>Shelf life:</strong> 24 months in unopened container, dry place, no sunlight</p>
    </div>
    
    <div class="col-6">
        <table class="table table-bordered">
            <tr>
                <th>Total Standard Items</th>
                <td class="text-right">{{ frappe.format(doc.total, {'fieldtype': 'Currency'}) }}</td>
            </tr>
            {% if escalated_items %}
            <tr>
                <th>Total Escalated Items</th>
                <td class="text-right">
                    {% set escalated_total = escalated_items|sum(attribute='amount') %}
                    {{ frappe.format(escalated_total, {'fieldtype': 'Currency'}) }}
                </td>
            </tr>
            {% endif %}
            <tr>
                <th>Grand Total</th>
                <td class="text-right">
                    {% set grand_total = doc.total + (escalated_items|sum(attribute='amount') if escalated_items else 0) %}
                    {{ frappe.format(grand_total, {'fieldtype': 'Currency'}) }}
                </td>
            </tr>
            <tr>
                <th>Rounded Total</th>
                <td class="text-right">{{ frappe.format(doc.rounded_total, {'fieldtype': 'Currency'}) }}</td>
            </tr>
        </table>
        
        <p><strong>In Words:</strong> {{ doc.in_words }}</p>
    </div>
</div>

<div class="footer">
    <p>Please confirm reception of this quotation and contact us if you need further information.</p>
    <p><strong>Thanks & Best Regards,</strong><br>
    <strong>Liliana Arredondo</strong><br>
    lily@amb-wellness.com</p>
</div>

<style>
    .header {
        text-align: center;
        margin-bottom: 20px;
        border-bottom: 2px solid #2E7D32;
        padding-bottom: 15px;
    }
    .header h2 {
        color: #2E7D32;
        margin-bottom: 5px;
    }
    .table {
        width: 100%;
        border-collapse: collapse;
        margin-bottom: 20px;
        font-size: 12px;
    }
    .table th {
        background-color: #2E7D32;
        color: white;
        padding: 8px;
        text-align: left;
        font-weight: bold;
    }
    .table td {
        padding: 8px;
        border: 1px solid #ddd;
    }
    .text-right {
        text-align: right;
    }
    .footer {
        margin-top: 30px;
        padding-top: 15px;
        border-top: 1px solid #ddd;
        font-size: 12px;
    }
    h4 {
        color: #2E7D32;
        margin-bottom: 15px;
        padding-bottom: 5px;
        border-bottom: 1px solid #2E7D32;
    }
    .list-unstyled {
        margin-left: 0;
        padding-left: 0;
        list-style: none;
    }
    .list-unstyled li {
        margin-bottom: 5px;
    }
    .row {
        display: flex;
        flex-wrap: wrap;
        margin-right: -15px;
        margin-left: -15px;
    }
    .col-6 {
        flex: 0 0 50%;
        max-width: 50%;
        padding: 0 15px;
    }
    hr {
        margin: 20px 0;
        border: 0;
        border-top: 1px solid #ddd;
    }
</style>

