html:
{% set escalated_items = doc.get("custom_quotation_child_table") or [] %}

<div class="header">
    <h2>Agromayal Botanica, S.A. de C.V.</h2>
    <p>GUANACEVI # 245<br>
    PARQUE INDUSTRIAL GOMEZ PALACIO<br>
    GOMEZ PALACIO DURANGO , MEXICO 35078<br>
    PH : +52-871-750-0846<br>
    FX : +52-871-750-1379<br>
    Email: oscar@amb-wellness.com<br>
    Web: www.amb-wellness.com</p>
</div>

<h3>Quotation No: {{ doc.name }}</h3>

<div class="row">
    <div class="col-6">
        <strong>To:</strong><br>
        {{ doc.customer_name }}<br>
        {% if doc.contact_display %}{{ doc.contact_display }}<br>{% endif %}
        {{ doc.address_display }}
    </div>
    <div class="col-6 text-right">
        <strong>Date:</strong> {{ doc.transaction_date }}<br>
        <strong>Valid Till:</strong> {{ doc.valid_till }}<br>
        <strong>Currency:</strong> {{ doc.currency }}
    </div>
</div>

<hr>

<!-- Standard Items Table (if any) -->
{% if doc.items %}
<h4>Standard Items</h4>
<table class="table table-bordered">
    <thead>
        <tr>
            <th>Sr</th>
            <th>Item Code</th>
            <th>Description</th>
            <th>Quantity</th>
            <th>UOM</th>
            <th>Rate</th>
            <th>Amount</th>
        </tr>
    </thead>
    <tbody>
        {% for item in doc.items %}
        <tr>
            <td>{{ loop.index }}</td>
            <td>{{ item.item_code }}</td>
            <td>{{ item.item_name }}{% if item.description %}<br><small>{{ item.description }}</small>{% endif %}</td>
            <td>{{ item.qty }}</td>
            <td>{{ item.uom }}</td>
            <td class="text-right">{{ frappe.format(item.rate, {'fieldtype': 'Currency'}) }}</td>
            <td class="text-right">{{ frappe.format(item.amount, {'fieldtype': 'Currency'}) }}</td>
        </tr>
        {% endfor %}
    </tbody>
    <tfoot>
        <tr>
            <td colspan="6" class="text-right"><strong>Total:</strong></td>
            <td class="text-right"><strong>{{ frappe.format(doc.total, {'fieldtype': 'Currency'}) }}</strong></td>
        </tr>
    </tfoot>
</table>
{% endif %}

<!-- Escalated Pricing Table -->
{% if escalated_items %}
<h4>Escalated Pricing - Quantity Based</h4>
<table class="table table-bordered">
    <thead>
        <tr>
            <th>Sr</th>
            <th>Item Code</th>
            <th>Description</th>
            <th>Quantity Range</th>
            <th>EX Works Price</th>
            <th>Freight Cost</th>
            <th>Delivered Price</th>
            <th>Total Price Range</th>
            <th>Freight Type</th>
        </tr>
    </thead>
    <tbody>
        {% for item in escalated_items %}
        <tr>
            <td>{{ loop.index }}</td>
            <td>{{ item.item_code }}</td>
            <td>{{ item.item_name }}</td>
            <td>{{ item.quantity_range }}</td>
            <td class="text-right">{{ frappe.format(item.ex_works_price, {'fieldtype': 'Currency'}) }}/kg</td>
            <td class="text-right">{{ frappe.format(item.freight_cost, {'fieldtype': 'Currency'}) }}/kg</td>
            <td class="text-right">{{ frappe.format(item.delivered_price, {'fieldtype': 'Currency'}) }}/kg</td>
            <td class="text-right">
                {% set min_total = item.delivered_price * item.min_quantity %}
                {% set max_total = item.delivered_price * item.max_quantity %}
                {% if item.min_quantity == item.max_quantity %}
                    {{ frappe.format(min_total, {'fieldtype': 'Currency'}) }}
                {% else %}
                    {{ frappe.format(min_total, {'fieldtype': 'Currency'}) }} - {{ frappe.format(max_total, {'fieldtype': 'Currency'}) }}
                {% endif %}
            </td>
            <td>{{ item.freight_type }}</td>
        </tr>
        {% endfor %}
    </tbody>
</table>
{% endif %}

<!-- Totals Section -->
<div class="row">
    <div class="col-6">
        <h5>Terms & Conditions</h5>
        <ul class="list-unstyled">
            <li><strong>Quotation valid until:</strong> December 31st, 2025</li>
            <li><strong>Lead time of production:</strong> 7-10 Days</li>
            <li><strong>Lead time of transportation:</strong> 3 Days</li>
            <li><strong>Incoterm:</strong> DAP-France (Importer responsible for import Duties & Taxes)</li>
            <li><strong>Freight terms:</strong> Air Freight via UPS courier (Door to Door)</li>
            <li><strong>HS CODE:</strong> 1302.19.99</li>
            <li><strong>Payment terms:</strong> 60 DAYS NET</li>
        </ul>
        
        <h5>Packaging:</h5>
        <p><strong>Standard:</strong> 25kg in double polyethylene bags inside carton fiber drum<br>
        <strong>Individual:</strong> 1kg in metalized bag</p>
        
        <p><strong>Shelf life:</strong> 24 months in unopened container, dry place, no sunlight</p>
    </div>
    
    <div class="col-6">
        <table class="table table-bordered">
            <tr>
                <th>Total Standard Items</th>
                <td class="text-right">{{ frappe.format(doc.total, {'fieldtype': 'Currency'}) }}</td>
            </tr>
            {% if escalated_items %}
            <tr>
                <th>Total Escalated Items</th>
                <td class="text-right">
                    {% set escalated_total = escalated_items|sum(attribute='amount') %}
                    {{ frappe.format(escalated_total, {'fieldtype': 'Currency'}) }}
                </td>
            </tr>
            {% endif %}
            <tr>
                <th>Grand Total</th>
                <td class="text-right">
                    {% set grand_total = doc.total + (escalated_items|sum(attribute='amount') if escalated_items else 0) %}
                    {{ frappe.format(grand_total, {'fieldtype': 'Currency'}) }}
                </td>
            </tr>
            <tr>
                <th>Rounded Total</th>
                <td class="text-right">{{ frappe.format(doc.rounded_total, {'fieldtype': 'Currency'}) }}</td>
            </tr>
        </table>
        
        <p><strong>In Words:</strong> {{ doc.in_words }}</p>
    </div>
</div>

<div class="footer">
    <p>Please confirm reception of this quotation and contact us if you need further information.</p>
    <p><strong>Thanks & Best Regards,</strong><br>
    <strong>Liliana Arredondo</strong><br>
    lily@amb-wellness.com</p>
</div>

<style>
    .header {
        text-align: center;
        margin-bottom: 20px;
        border-bottom: 2px solid #2E7D32;
        padding-bottom: 15px;
    }
    .header h2 {
        color: #2E7D32;
        margin-bottom: 5px;
    }
    .table {
        width: 100%;
        border-collapse: collapse;
        margin-bottom: 20px;
        font-size: 12px;
    }
    .table th {
        background-color: #2E7D32;
        color: white;
        padding: 8px;
        text-align: left;
        font-weight: bold;
    }
    .table td {
        padding: 8px;
        border: 1px solid #ddd;
    }
    .text-right {
        text-align: right;
    }
    .footer {
        margin-top: 30px;
        padding-top: 15px;
        border-top: 1px solid #ddd;
        font-size: 12px;
    }
    h4 {
        color: #2E7D32;
        margin-bottom: 15px;
        padding-bottom: 5px;
        border-bottom: 1px solid #2E7D32;
    }
    .list-unstyled {
        margin-left: 0;
        padding-left: 0;
        list-style: none;
    }
    .list-unstyled li {
        margin-bottom: 5px;
    }
    .row {
        display: flex;
        flex-wrap: wrap;
        margin-right: -15px;
        margin-left: -15px;
    }
    .col-6 {
        flex: 0 0 50%;
        max-width: 50%;
        padding: 0 15px;
    }
    hr {
        margin: 20px 0;
        border: 0;
        border-top: 1px solid #ddd;
    }
</style>

{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead, footer, print_settings=None, print_heading_template=None) -%}
	{% if letter_head and not no_letterhead %}
		<div class="letter-head">{{ letter_head }}</div>
	{% endif %}
	{% if print_heading_template %}
		{{ frappe.render_template(print_heading_template, {"doc":doc}) }}
	{% endif %}
{%- endmacro -%}

{% for page in layout %}
<div class="page-break invoice-wrapper">
	<div {% if print_settings.repeat_header_footer %} id="header-html" class="hidden-pdf" {% endif %}>
		{{ add_header(loop.index, layout|len, doc, letter_head, no_letterhead, footer, print_settings) }}
	</div>
	{%- if doc.meta.is_submittable and doc.docstatus==2-%}
		<div class="text-center" document-status="cancelled">
			<h4 style="margin-top: 20px;">{{ _("CANCELLED") }}</h4>
		</div>
	{%- endif -%}
	{%- if doc.meta.is_submittable and doc.docstatus==0 and (print_settings==None or print_settings.add_draft_heading) -%}
		<div class="text-center" document-status="draft">
			<h4 style="margin-top: 20px !important;">{{ _("DRAFT") }}</h4>
		</div>
	{%- endif -%}

	<style>
		.letter-head {
			margin-top: 12px !important;
		}
		.invoice-wrapper {
			font-family: "Inter", sans-serif;
			color: var(--black-overlay-700);
		}

		.print-format-body {
			padding: 30px 12px 12px 12px !important;
		}

		body {
			margin-bottom: 0mm !important;
			padding: 0px !important;
		}

		table.info-table,
		table.items-table,
		table.tax-table {
			border-collapse: separate;
			border-spacing: 0;
			border: 1px solid #ededed;
			border-radius: 10px;
			overflow: hidden;
		}

		table.info-table td {
			border-bottom: 1px solid #ededed;
			padding: 8px 10px !important;
		}
		table.items-table td,
		table.tax-table td {
			border-bottom: 1px solid #ededed;
			padding: 8px;
		}

		table.info-table td:not(:first-of-type),
		table.items-table td:not(:first-of-type),
		table.tax-table td:not(:first-of-type) {
			border-left: 1px solid #ededed;
		}

		table.info-table tbody tr:last-of-type td,
		table.items-table tbody tr:last-of-type td,
		table.tax-table tbody tr:last-of-type td {
			border-bottom: none;
		}

		thead.table-header {
			background: #f8f8f8;
		}
		thead.table-header td {
			color: #7c7c7c;
		}

		.text-center {
			text-align: center;
		}
		.text-left {
			text-align: left;
		}
		.text-right {
			text-align: right;
		}
		.text-muted {
			color: #7c7c7c;
		}
		.text-dark {
			color: #525252;
		}
		.text-bold {
			font-weight: bold;
		}
		.small-text {
			color: #666;
		}

		.mt-10 {
			margin-top: 10px;
		}
		.mt-15 {
			margin-top: 15px;
		}
		.mt-20 {
			margin-top: 20px;
		}
		.mt-40 {
			margin-top: 40px;
		}
		.mt-80 {
			margin-top: 80px;
		}
		.mb-20 {
			margin-bottom: 20px;
		}
		.pb-8 {
			padding-bottom: 8px;
		}
		.amount-width {
			width: 110px;
		}

		.totals-table {
			border-radius: 5px;
			width: 100%;
		}
		.totals-table td {
			padding: 4px 8px;
		}
		.totals-table tr {
			border-bottom: 1px solid #ededed;
			position: relative;
		}
		.totals-table tr td {
			position: relative;
		}
		.totals-table tr td div {
			width: 12%;
			height: 14px;
			background-color: white;
			position: absolute;
			bottom: -6px;
			left: 0%;
			color: white;
		}
		.totals-table tr:last-child {
			border-bottom: none;
		}
		.totals-table .grand-total td {
			padding: 6px 8px;
			font-weight: bold;
		}

		.words-box {
			background: #f8f8f8;
			border-radius: 8px;
			padding: 8px;
			line-height: 21px;
			letter-spacing: 0.21px;
			font-size: small;
		}

		.info-card {
			color: #7c7c7c;
		}

		.title {
			color: #7c7c7c !important;
		}

		.heading {
			color: #525252 !important;
			font-weight: 300;
		}

		.print-format {
			color: #171717;
			font-size: 14px;
			font-style: normal;
			font-weight: 420;
			line-height: 21px;
			padding: 0px;
			letter-spacing: 0.14px;
			margin-left: 0mm !important;
			margin-right: 0mm !important;
		}
		.letter-head-footer {
			margin-top: 0px !important;
		}
		.print-heading {
			margin-top: 0px !important;
			margin-bottom: 0px !important;
			padding-bottom: 0px !important;
		}
	</style>
	<div class="print-format-body">
		<table class="info-table mb-20" style="width: 100%">
			<tr>
				<td style="width: 50%">
					<span class="heading">{{ _("Customer Name") }}:</span> {{doc.customer_name }}
				</td>
				<td style="width: 50%">
					<span class="heading">{{ _("Payment Due Date") }}:</span> {{
					frappe.utils.format_date(doc.due_date) }}
				</td>
			</tr>
			<tr>
				<td><span class="heading">{{ _("Invoice Number") }}:</span> {{ doc.name }}</td>
				<td>
					<span class="heading">{{ _("Invoice Date") }}:</span> {{
					frappe.utils.format_date(doc.posting_date) }}
				</td>
			</tr>
			<tr>
				<td><span class="heading">{{ _("Bill From") }}:</span><br />
					{% if doc.company_address %}
                        {% set company_address = frappe.db.get_value("Address", doc.company_address, ["address_line1", "address_line2", "city", "state", "pincode", "country"], as_dict=True) %}
                        {{  doc.company }}<br>
                        {{ company_address.get("address_line1") or "" }}<br>
                        {% if company_address.get("address_line2") %}{{ company_address.get("address_line2") }}<br>{% endif %}
                        {{ company_address.get("city") or "" }}, {{ company_address.get("state") or "" }} {{ company_address.get("pincode") or "" }}, {{ company_address.get("country") or "" }}<br>
                    {% endif %}
				</td>
				<td><span class="heading">{{ _("Bill To") }}:</span><br />
				    {% if doc.customer_address %}
						{% set customer_address = frappe.db.get_value("Address", doc.customer_address, ["address_line1", "address_line2", "city", "state", "pincode", "country"], as_dict=True) %}
                        {{ doc.customer_name }}<br>
						{{ customer_address.address_line1 or "" }}<br>
						{% if customer_address.address_line2 %}{{ customer_address.address_line2 }}<br>{% endif %}
						{{ customer_address.city or "" }} {{ customer_address.state or "" }} {{ customer_address.pincode or "" }} {{ customer_address.country or "" }}<br>
					{% endif %}
				</td>
			</tr>
		</table>

		<!-- Items Table -->
		{% set item_naming_by = frappe.db.get_single_value("Stock Settings", "item_naming_by") %}
		<table class="items-table mt-15" style="width: 100%">
			<thead class="table-header">
				<tr>
					<td class="text-center">{{ _("No") }}</td>
					<td class="text-left">{{ _("Item") }}</td>
					{% if item_naming_by != "Item Code" %}
						<td class="text-left">{{ _("Item Code") }}</td>
					{% endif %}
