def handle_doctype_event(doc, method):
    """Called from document hooks"""
    webhooks = frappe.get_all("Raven Webhook Handler",
        filters={
            "enabled": 1,
            "doc_type": doc.doctype
        },
        pluck="name"
    )
    
    for webhook_name in webhooks:
        webhook = frappe.get_doc("Raven Webhook Handler", webhook_name)
        
        # Get enabled events for this webhook
        enabled_events = [
            row.event for row in webhook.trigger_events 
            if row.enabled and row.webhook_handler == webhook.name
        ]
        
        if method.replace("on_", "").replace("_", " ") in enabled_events:
            process_webhook(webhook, doc)

import frappe
import hmac
import hashlib
import requests

@frappe.whitelist(allow_guest=True)  # Allow OpenAI to call this
def handler():
    # Security: Verify HMAC signature
    secret = frappe.get_conf().webhook_secret
    signature = frappe.get_request_header("X-Signature")
    payload = frappe.request.get_data()
    
    computed_sig = hmac.new(
        secret.encode(),
        payload,
        hashlib.sha256
    ).hexdigest()
    
    if not hmac.compare_digest(signature, computed_sig):
        frappe.throw("Invalid signature", frappe.AuthenticationError)

    # Process OpenAI request
    data = frappe.parse_json(payload)
    response = requests.post(
        "https://api.openai.com/v1/chat/completions",
        headers={
            "Authorization": f"Bearer {get_openai_key()}",
            "Content-Type": "application/json"
        },
        json={
            "model": "gpt-4",
            "messages": [{"role": "user", "content": data.get("prompt")}]
        }
    )
    
    return response.json()

def get_openai_key():
    return frappe.get_doc("Third Party API", "OpenAI").get_password("api_key")

