                'manufacturing_date', 'production_workstation', 'batch_status',
                'sales_order_related', 'creation'
            ],
            order_by='creation desc',
            limit=3
        )
        
        announcements = []
        
        for batch in running_batches:
            try:
                # Get related sub-lots (Level 2)
                sub_lots = frappe.get_all(
                    'Batch AMB',
                    filters={
                        'parent_batch_amb': batch.name,
                        'custom_batch_level': '2'
                    },
                    fields=['custom_generated_batch_name'],
                    limit=1
                )
                
                # Get sales order info
                sales_order = batch.sales_order_related or 'N/A'
                if batch.work_order_ref and sales_order == 'N/A':
                    try:
                        work_order = frappe.get_doc('Work Order', batch.work_order_ref)
                        sales_order = work_order.sales_order if hasattr(work_order, 'sales_order') and work_order.sales_order else 'N/A'
                    except:
                        sales_order = 'N/A'
                
                # Format manufacturing date
                manufacturing_date = 'N/A'
                if batch.manufacturing_date:
                    try:
                        date_obj = getdate(batch.manufacturing_date)
                        manufacturing_date = date_obj.strftime('%Y-%m-%d')
                    except:
                        manufacturing_date = str(batch.manufacturing_date)
                
                # Create announcement text
                announcement_text = f"""ðŸ­ LOTE EN PRODUCCION: {batch.custom_generated_batch_name or batch.name}
ðŸ“‹ ORDEN DE TRABAJO: {batch.work_order_ref or 'N/A'}
ðŸ“¦ NUMERO DE SUB-LOTE: {sub_lots[0].custom_generated_batch_name if sub_lots else 'N/A'}
ðŸ›’ PEDIDO DE VENTA: {sales_order}
âš¡ STATUS: {batch.batch_status}
ðŸ“ ESTACIÃ“N: {batch.production_workstation or 'N/A'}
ðŸ“… FECHA: {manufacturing_date}"""
                
                announcements.append({
                    'title': f'Batch {batch.custom_generated_batch_name or batch.name}',
                    'content': announcement_text,
                    'batch_name': batch.name,
                    'priority': 'high',
                    'timestamp': frappe.utils.now()
                })
                
            except Exception as e:
                frappe.log_error(f"Error processing batch {batch.name}: {str(e)}", "Batch Announcement Error")
                continue
        
        return {
            'success': True,
            'announcements': announcements,
            'count': len(announcements),
            'timestamp': frappe.utils.now()
        }
        
    except Exception as e:
        frappe.log_error(f"Error in get_running_batch_announcements: {str(e)}", "Batch Announcement Error")
        return {
            'success': False,
            'error': str(e),
            'announcements': [],
            'count': 0
        }

# Server Script for Quotation Item Escalated calculations
# Enabled: Yes
# Reference Doctype: Quotation Item Escalated
# Script Type: API

def calculate_escalated_prices(doc, method):
    # Calculate freight cost based on quantity
    freight_cost = calculate_freight_cost(doc.qty)
    doc.freight_cost = freight_cost
    
    # Apply quantity-based escalation
    escalated_rate = apply_quantity_escalation(doc.price_list_rate, doc.qty)
    doc.escalated_rate = escalated_rate
    
    # Calculate final rate
    doc.rate = flt(escalated_rate) + flt(freight_cost)
    
    # Calculate amount
    if doc.qty and doc.rate:
        doc.amount = flt(doc.qty) * flt(doc.rate)
        
        # Calculate net amount if discount exists
        if doc.discount_percentage:
            doc.discount_amount = flt(doc.amount) * flt(doc.discount_percentage) / 100
            doc.net_amount = flt(doc.amount) - flt(doc.discount_amount)
            
            if doc.qty:
                doc.net_rate = flt(doc.net_amount) / flt(doc.qty)

def calculate_freight_cost(quantity):
    quantity = flt(quantity)
    if quantity <= 1:
        return 58.30
    elif quantity <= 5:
        return 19.87
    elif quantity <= 10:
        return 16.06
    elif quantity <= 20:
        return 14.17
    else:
        return 13.91

def apply_quantity_escalation(base_price, quantity):
    base_price = flt(base_price)
    quantity = flt(quantity)
    
    if quantity >= 100:
        return base_price * 0.9  # 10% discount
    elif quantity >= 50:
        return base_price * 0.95  # 5% discount
    else:
        return base_price

# Hook this function to validate event
calculate_escalated_prices

class QuotationItemEscalated(Document):
    def validate(self):
        self.set_quantity_limits()
        self.calculate_prices()
    
    def set_quantity_limits(self):
        """Set min and max quantity based on selected range"""
        if self.quantity_range:
            ranges = {
                '1-5 kg': (1, 5),
                '6-10 kg': (6, 10),
                '11-25 kg': (11, 25),
                '26-200 kg': (26, 200)
            }
            
            if self.quantity_range in ranges:
                self.min_quantity, self.max_quantity = ranges[self.quantity_range]
    
    def calculate_prices(self):
        """Calculate all prices based on quantity range"""
        if self.ex_works_price and self.freight_cost and self.min_quantity:
            # Calculate delivered price
            self.delivered_price = flt(self.ex_works_price) + flt(self.freight_cost)
            
            # Calculate price per kg
            self.price_per_kg = self.delivered_price
            
            # Calculate total price range
            if self.min_quantity == self.max_quantity:
                self.total_price = self.delivered_price * self.min_quantity
            else:
                # Store the maximum total for the range
                self.total_price = self.delivered_price * self.max_quantity
    
    def calculate_freight_based_on_type(self):
        """Calculate freight cost based on freight type and quantity range"""
        if self.quantity_range and self.freight_type:
            freight_matrix = {
                '1-5 kg': {
                    'Air Freight': 58.30,
                    'Sea Freight': 45.00,
                    'Ground': 35.00,
                    'Door to Door': 60.00
                },
                '6-10 kg': {
                    'Air Freight': 19.87,
                    'Sea Freight': 15.00,
                    'Ground': 12.00,
                    'Door to Door': 22.00
                },
                '11-25 kg': {
                    'Air Freight': 16.06,
                    'Sea Freight': 12.00,
                    'Ground': 9.00,
                    'Door to Door': 18.00
                },
                '26-200 kg': {
                    'Air Freight': 13.91,
                    'Sea Freight': 10.00,
                    'Ground': 7.00,
                    'Door to Door': 15.00
                }
            }
            
            if (self.quantity_range in freight_matrix and 
                self.freight_type in freight_matrix[self.quantity_range]):
                self.freight_cost = freight_matrix[self.quantity_range][self.freight_type]

import frappe

@frappe.whitelist()
def get_running_batch_announcements():
    """Get running batch announcements for navbar widget"""
    try:
        # Check if Batch AMB doctype exists
        if not frappe.db.exists("DocType", "Batch AMB"):
            return {"success": False, "message": "Batch AMB doctype not found"}

        # Get running batches
        running_batches = frappe.get_all("Batch AMB", 
            filters={"quality_status": "Running"}, 
            fields=["name", "title", "batch_id", "production_status", "priority", "start_time"]
        )

        announcements = []
        for batch in running_batches:
            announcements.append({
                "title": batch.title or batch.name,
                "content": f"ðŸ”§ Batch: {batch.batch_id or 'N/A'}\nâ± Status: {batch.production_status or 'Running'}\nðŸ• Started: {batch.start_time or 'N/A'}",
                "priority": batch.priority or "medium"
            })

        return {
            "success": True,
            "announcements": announcements,
            "count": len(announcements)
        }

    except Exception as e:
        frappe.log_error("Batch Announcements Error", str(e))
        return {"success": False, "message": str(e)}

